				keyValues.put("method", alipayBean.getMethod());

				keyValues.put("sign_type", alipayBean.getSign_type());

				Log.d("OrderInfoUtil2_0", "===========curDate=======" + alipayBean.getTimestamp());
				keyValues.put("timestamp", alipayBean.getTimestamp());
